            new DrWhoQuote { Quote = "Where's your joy in life? Where's your optimism? … Whenever you <br>go into a new situation, you must always believe the best until <br>you find out exactly what the situation's all about. <br>Then, believe the worst.", Doctor = "The Fourth Doctor" },
            new DrWhoQuote { Quote = "Sometimes my brilliance astonishes even me.", Doctor = "The Fourth Doctor" },
            new DrWhoQuote { Quote = "Someone once tried to build a machine as efficient as the brain. <br>The only problem was, it would have had to be bigger than<br>London — do you remember London? — and powered by the entire <br>European grid. And that was just a human brain. <br>Mine's much more complex.", Doctor = "The Fourth Doctor" },
            new DrWhoQuote { Quote = "The Key to Time is still in my possession. Rage all you like!", Doctor = "The Fourth Doctor" },
            new DrWhoQuote { Quote = "I'm a very dangerous fellow when I don't know what I'm doing.", Doctor = "The Fourth Doctor" },
