            new DrWhoQuote { Quote = "Among all the varied wonders of the universe, there's nothing so firmly <br>clamped shut as the military mind.", Doctor = "The Seventh Doctor" },
            new DrWhoQuote { Quote = "All is change, all is movement.", Doctor = "The Seventh Doctor" },
            new DrWhoQuote { Quote = "There are worlds out there where the sky is burning, and the sea's <br>asleep, and the rivers dream; people made of smoke and cities made of song. <br>Somewhere there's danger, somewhere there's injustice, somewhere else <br>the tea's getting cold. Come on, Ace. We've got work to do.", Doctor = "The Seventh Doctor" },
            new DrWhoQuote { Quote = "I am not like you.", Doctor = "The Eighth Doctor" },
            new DrWhoQuote { Quote = "Who am I? WHO — AM — I?", Doctor = "The Eighth Doctor" },
            new DrWhoQuote { Quote = "Grace, I came back to life before your eyes. I held back death. Look, <br>I can't make your dream come true forever, but I can make <br>it come true today.", Doctor = "The Eighth Doctor" },
            new DrWhoQuote { Quote = "I love humans. Always seeing patterns in things that aren't there.", Doctor = "The Eighth Doctor" },
